INSERT INTO `arts` VALUES ('Academic English Studies', 'ACADENG 100', 'Forms in Academic English', 'Focuses on developing an understanding of academic reading and writing, including sentence and paragraph structure and academic vocabulary, and aims to develop strategies for employing these for effective reading and writing of academic texts. Develops an understanding of broad principles and practices of academic discourse at university level.', 'Restriction: May not be taken if ENGWRIT 101 or ESOL 201 or ACADENG 201 or ESOL 210 or ACADENG 210 has previously been passed. This course is available only to students who speak English as an additional language', 'null,ssssssssssssssss,yyy,FFFFFFFFFFFFFFFFF,FFFFFFFFFFFFFFFFF,FFFFFFFFFFFFFFFFF,FFFFFFFFFFFFFFFFF,2020', 'null');
INSERT INTO `arts` VALUES ('Academic English Studies', 'ACADENG 101', 'Academic English Writing', 'Teaches students the skills necessary to write essays of exposition and argument for university purposes. It includes brainstorming, writing an outline, structuring an essay, integrating quotations, summaries and referencing.', 'Restriction: May not be taken if ACADENG 93F, 201, 210, ENGWRIT 101, ESOL 201, 210 has previously been passed. This course is available only to students who speak English as an additional language', 'null', 'null');
INSERT INTO `arts` VALUES ('Academic English Studies', 'ACADENG 104', 'Academic English for Business', 'Focuses on core English academic reading and writing skills, and strategies for learning disciplinary vocabulary. Targets the academic literacy needs of students in accessing the undergraduate business curriculum and develops awareness of appropriate text structures and academic style to understand and express business-related concepts in an academic context.', 'Restriction: May not be taken if ENGWRIT 101 or ESOL 201 or ACADENG 201 or ESOL 210 or ACADENG 210 has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Academic English Studies', 'ACADENG 210', 'Writing Research Reports', 'Aims to develop skills needed for writing research and laboratory reports. It covers key stages in writing a standard report and the language patterns associated with each of these stages. Course components include writing the literature review, methodology, results and discussion sections of a report, dissertation or thesis.', 'Prerequisite: ACADENG 101 or approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Academic English Studies', 'ACADENG 212', 'Special Topic', '', 'Restriction: ESOL 212. This course is available only to students who speak English as an additional language', 'null', 'null');
INSERT INTO `arts` VALUES ('Academic Integrity', 'ACADINT A01', 'Academic Integrity Course', 'The Academic Integrity Course is an online course designed to increase student knowledge of academic integrity, university rules relating to academic conduct, and the identification and consequences of academic misconduct. Students work through a series of modules, outlining scenarios that they may encounter while studying at university. Each scenario provides information on relevant rules, resources and expected behaviour.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 100', 'Ancient Egyptian History', 'A broad overview of ancient Egyptian society and history. It encompasses the approximately 2000 years between the early period of formation of the state of Egypt and the end of the New Kingdom. A focus on political history forms the framework for discussions of the art, literature, and religion of the period.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 102', 'Ancient Greek History', 'An introduction to Greek history and civilisation from the Bronze Age to the death of Alexander the Great utilising both archaeological evidence and literary sources.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 103', 'Roman History', 'An introduction to the civilisation and history of Ancient Rome, with particular reference to the Republic and Early Empire.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 110', 'Dynasties, Democracy, Empire', 'Explores the history and cultures of three civilisations in the ancient Mediterranean: Egypt, Greece, and Rome. Uses ancient evidence, from the newest archaeological discoveries, to the works of classical literature, to present major historical events in the Mediterranean against the backdrop of the everyday lives of the people who lived in these societies.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 200', 'Egypt in Dynasty XVIII', 'The cosmopolitan nature of ancient Egypt during the era of its imperial expansion and its consciousness as a world power. Topics covered include the social, political, and economic ramifications of this sudden change in Egypt\'s role in the ancient world. The emergence of new religious directions will also be discussed, among which are the state cults of Amun-Re and the religious monotheism of Akhenaton.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 202', 'Greece and Persia', 'A study of the contacts and conflicts between Greece and Persia 560-323BC.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 210', 'Egyptian Language 1A', 'A study of the Egyptian language, hieroglyphic writing and selected documents up to Dynasty 18.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 220', 'Egyptian Language 1B', 'Further study of the Egyptian language, hieroglyphic writing and selected documents up to Dynasty 18.', 'Prerequisite: ANCHIST 210', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 230', 'Egypt in Dynasties XI-XVII', 'Covers the history of Egypt during one major phase of unification, the Middle Kingdom, as well as the First and Second Intermediate Periods. Topics analysed include the rise of imperialism in the south (Nubia), the importance of literature of propaganda as a social bond of loyalty, the conquest of the north by the Asiatic Hyksos, and the new artistic style of portraiture.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 249', 'Special Topic', '', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 251', 'Icon and Narrative: Egypt, Greece, and the Aegean', 'A study of key periods and cultural developments in the art and archaeological remains of Egypt (from the Predynastic Period to the New Kingdom) and Greece and the Aegean island cultures of the Bronze Age (from c. 4000 BC to c. 1000 BC).', 'Prerequisite: 15 points at Stage I in Ancient History, Classical Studies, or Art History, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 252', 'Egyptian Religion', 'A study of ancient Egyptian religion from the Early Dynastic period through to the end of the Late Period. The course will examine religious practice as well as religious thought, and will consider the patterns of belief throughout the ancient period of Egypt\'s history.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 253', 'Early Egypt', 'Covers the earliest periods of Egypt\'s development from the prehistoric period to the end of the Old Kingdom. This course focuses on the lead-up to state formation and the great Pyramid Age that followed.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 254', 'Early Rome', 'A study of the earliest development of ancient Rome using written sources but with special emphasis on archaeological evidence.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 255', 'The Later Roman Empire', 'A study of the Roman empire between the third and sixth centuries CE. Topics covered include the social, economic and political crises of the period, encounters and struggles between Romans and barbarians, the conflict between Paganism and Christianity, and the emergence of the barbarian kingdoms in the West and the Byzantine empire in the East.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 256', 'The Ancient World at War', 'Provides an in-depth analysis of the role of the military in ancient Egypt, Greece and Rome. The physical evidence of warfare as well as chronological development of warfare within each society will be discussed. An additional theme will be the interweaving of the social and cultural impact of warfare and the army upon these civilisations.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 257', 'Special Topic in Egyptian History', '', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 258', 'Fifth Century Athens', 'The social, economic, political, and ideological development of Athens and Athenian democracy in the fifth century BC; the course will consider both literary sources and archaeological material.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 259', 'Special Topic in Roman History', '', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 260', 'Roman Revolutions', 'Covers the history, politics, society and culture of Rome during the late Republic and early Imperial periods. Topics include the army, religion, family, sexuality, literature, art and the life of the provinces, set against the dramatic breakdown of old systems of government and their replacement with a new model of rule.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 261', 'Special Topic: The City in the Ancient World', '', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 262', 'The Ancient Economy', 'A study of the economies of the ancient Egyptians, Greeks and Romans. Students will explore the principal ways in which we try to understand economic activity through theories concerning market-exchange, redistribution of resources and reciprocity to the practical relationships that linked the economy to society. The course will focus on a variety of different evidence including archaeology, inscriptions, papyrological material and numismatics as well as ancient literature.', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 263', 'Special Topic', '', 'Prerequisite: 15 points at Stage I in Ancient History or Classical Studies, and 30 points passed', 'null', 'null');
