INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 310', 'Egyptian Language 2A', 'A study of the historical inscriptions covering the key pharaonic records of Dynasty XVIII, for example, of Thutmose III and Amunhotep II and non-royal individuals of the XVIIIth Dynasty. Texts include the war records, building inscriptions, the Hymn to the Aten and the various sphinx stelae.', 'Prerequisite: ANCHIST 220', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 314', 'Historians of Rome', 'A study of the Greek and Latin writers on Roman history in English translation; writers will include Livy, Sallust and Tacitus.', 'Prerequisite: 15 points at Stage II in Ancient History, Classical Studies, or Classical Studies and Ancient History, or 30 points at Stage II in Latin', 'null', 'null');
INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 324', 'Historians of Greece', 'A study of the Greek writers on Greek history in English translation; writers will include Herodotus, Thucydides and Xenophon.', 'Prerequisite: 15 points at Stage II in Ancient History, Classical Studies, or Classical Studies and Ancient History, or 30 points at Stage II in Greek', 'null', 'null');
