INSERT INTO `arts` VALUES ('Ancient History', 'ANCHIST 340', 'Egyptian Language 2B', 'A study of the main literary texts of the XIIth Dynasty such as The Story of Sinuhe and Cheops and the Magicians. Emphasis is placed upon various literary elements of these compositions such as folktale elements, the use of propaganda, the individual in society and the exotic foreign settings to the tales.', 'Prerequisite: ANCHIST 220', 'null', 'null');
