INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 701B', 'Human Palaeoecology', 'Critical survey of methods, theories and problems in human palaeoecology, including issues of resource use, landscape change, island colonisation and anthropogenic extinctions.', 'To complete this course students must enrol in ANTHRO 701 A and B, or ANTHRO 701', 'null', 'null');
