INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 703B', 'Landscape Archaeology', 'Uses geographic information systems (GIS) and other computer programmes to examine the spatial organisation of data, and the relationship of archaeological features both to other features, and the environment. The social processes underlying these spatial configurations will be a particular focus.', 'To complete this course students must enrol in ANTHRO 703 A and B, or ANTHRO 703', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 704A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 704B', 'Material Culture', 'The study of material culture using museum, ethnographic, archaeological and experimental approaches, including the information provided by material culture studies on human agency and the structuring of societies.', 'To complete this course students must enrol in ANTHRO 704 A and B', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 708A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 708B', 'Cultural Resource Management in Archaeology - Level 9', 'Covers all aspects of cultural resource management as it relates to archaeological sites and heritage with a particular focus on New Zealand archaeology and Mori heritage. There is an emphasis on site identification, recording and interpretation in the field. Legal aspects and the roles of archaeologists and iwi in cultural resource management are also covered.', 'To complete this course students must enrol in ANTHRO 708 A and B', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 709', 'Applying Anthropology - Level 9', 'Considers the diverse fields in which Anthropology may be applied to peoples and cultures in the contemporary world, including, for example: environmental and development issues; land and resource conflicts; mediation and advocacy; human rights; cultural heritage; social policy; business and industry; communications; marketing; medical investigations; museums and other representational activities. Addresses practical and ethical issues that arise in these areas.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 712', 'Topic in Biological Anthropology', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 713', 'Special Topic in Biological Anthropology', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 718A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 718B', 'Interpreting Biocultural Data', 'A survey of the design, implementation, analysis, interpretation, and dissemination of research in biocultural anthropology. It provides a holistic overview of both qualitative and quantitative approaches to biocultural anthropological scholarship.', 'To complete this course students must enrol in ANTHRO 718 A and B', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 719', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 719A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 719B', 'Ethnographic Practice and Design', 'Based on seminars, workshops and field research, the course prepares students to understand the foundations of anthropological ethnography and the ethical issues it entails, and to become proficient ethnographers in the field, in archives and at the desk. The course provides instruction and practice in research design and proposal writing in socio-cultural anthropology.', 'Restriction: ANTHRO 753', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 724', 'Special Topic in Social Anthropology', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 726A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 726B', 'Advanced Biological Anthropology', 'A critical review of key theoretical underpinnings and current debates in biological anthropology including Darwinism, population biology, adaptation and adaptability, evolutionary perspectives on modern humans and primatology.', 'Restriction: ANTHRO 710, 751, 752', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 727', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 727A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 727B', 'Ethnographies of Music-making', 'Advanced theories and methodologies for the ethnomusicological analysis of live musical performances and other behaviours across all genres and cultures. Primary attention is given to ethnography and participant-observation supported by analysis of industrial, cultural, musical, and mediated phenomena.', 'Prerequisite: 30 points from ANTHRO 323, 333, 357', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 728', 'Topic in Ethnomusicology', '</p>', 'null', 'null', 'null');
