INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 729B', 'Special Studies in Anthropology', 'A directed reading and individual study course to prepare students in the methodologies in a selected sub-discipline of Anthropology, under supervision of appropriate staff.', 'To complete this course students must enrol in ANTHRO 729 A and B, or ANTHRO 729', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 732', 'Reading Medical Ethnography', 'Examines the social anthropological practice of ethnography of health and illness in community and clinical settings, including \'non-Western\' and \'Western\' cultural contexts, through critical readings of recent ethnographies in medical anthropology. Considers ethnographic and anthropological theory, ethics, methodology and application.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 733', 'Research in Popular Music Culture', 'Advanced ethnomusicological theories and methodologies for the analysis of data that are obtained from mediated performance, archival sources, material culture and recorded music and image.', 'Prerequisite: 30 points from ANTHRO 323, 333, 357', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 735', 'Special Topic in Anthropology', '</p>', 'null', 'null', 'null');
