INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 736A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 736B', 'Special Studies in Anthropology', 'A directed reading and individual study course to prepare students in the methodologies in a selected sub-discipline of Anthropology, under supervision of appropriate staff.', 'To complete this course students must enrol in ANTHRO 736 A and B, or ANTHRO 736', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 738', 'Anthropology and World Religions', 'Examines the relationships between global religious bodies and theologies and local belief and practices. Addresses questions of agency, culture, power, cross-cultural encounters and vernacularisation in world religions such as, Christianity, Buddhism and Islam or themes such as, missionaries, vernacularisation and colonialism.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 739', '', '</p>', 'null', 'null', 'null');
