INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 739B', 'Special Studies in Anthropology', 'A directed reading and individual study course to prepare students in the methodologies in a selected sub-discipline of Anthropology under supervision of appropriate staff.', 'To complete this course students must enrol in ANTHRO 739 A and B, or ANTHRO 739', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 742', 'Contact and Colonialism', 'A seminar focused on critical understanding of the political, social and economic expansion of European countries around the world and its cultural consequences. Themes may include: cultural encounter, causes and effects of colonisation, interpretations of the other by colonisers and colonised, Creole cultures, slavery, race, resistance and accommodation, gender, demography, environmental impacts.', 'Restriction: ANTHRO 720', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 744', 'Anthropology and History', 'A critical examination of the characteristics, applications and interactions of different media in the production of the past: chant, genealogy, song, dance, textiles, carving, architecture, as well as written text.', 'Restriction: ANTHRO 720', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 745', 'Special Topic: Anthropology and the Humanities', 'Explores the interactions between anthropology and the humanities, especially literature and visual arts. Topics include anthropology\'s formation in relation to modernism, primitivism; how these movements have influenced nationalisms; contemporary exercises in genre-bending: ethnographic novels, ethnography as literature, televisual ethnography; the borders between empirical ethnographic and imaginative accounts. Students will consider how and why disciplinary boundaries are formed and transgressed.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 746', 'Special Topic: The Archaeology of the Anthroprocene', 'Calls for the Anthropocene, a new geological epoch, recognise long-term, consequential effects of human societies, regardless of size, economics or socio-political complexity, on environments, organisms and ecosystems. When did the Anthropocene begin? How do we track socio-natural interactions over deep time? What might the past offer the future? This course explores how archaeology contributes to these and related questions.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 747', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 748', 'Human Osteology', 'Advanced method and theory in human osteology. Coursework is a combination of seminars and practical workshops covering the areas of biocultural frameworks, ethics, taphonomy, human identification, dental anthropology, palaeopathology and biomolecular approaches. Work is focused upon method and theory as applied in the southern hemisphere.', 'Restriction: ANTHRO 730', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 749', 'Advanced Primatology', 'A practical and theoretical exploration of the methodological principles and research methods in contemporary primatology. Students build a working understanding of behavioural data collection and analysis, as well as developing tools for the assessment of populations and habitats.', 'Restriction: ANTHRO 730', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 753', 'Ethnographic Research', 'Students learn observational, ethnographic and quantitative social anthropological research methods by designing and carrying out a small class research project. Ethical and methodological issues are introduced.', 'Restriction: ANTHRO 711, 734', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 756', 'Anthropology and Intellectual Property', 'Examines recent anthropological contributions to debates about intellectual property. These include concepts of ownership, the objectification and appropriation of indigenous knowledge, creativity, bioprospecting, the protection of intangible cultural property, and the effects of global flows of information on persons, privacy and the ownership of ideas.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 758', 'Special Topic in Archaeology', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 759', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 760', 'Anthropological Theory and the Contemporary World', 'An analysis of foundational and current theoretical works in social anthropology and their relevance to understanding contemporary societies and cultures. The course examines anthropological approaches to long-standing disciplinary debates and contemporary issues of wider public debate.', 'Prerequisite: Permission of Head of Department', 'null', 'null');
INSERT INTO `arts` VALUES ('Anthropology', 'ANTHRO 761', 'Cultural Worlds in Anthropological Perspective', 'Draws on both classic and cutting edge approaches in anthropology to examine how human social and cultural worlds are constituted, contested and changed. The course introduces students to a range of different anthropological perspectives on contemporary social, political and economic issues and shows how these can be addressed cross-culturally.', 'Prerequisite: Permission of Head of Department', 'null', 'null');
