INSERT INTO `arts` VALUES ('Art History', 'ARTHIST 700B', 'Participation, Collaboration, and Photography', 'Explores a range of increasingly prevalent artistic practice grounded in artistic collaboration and audience participation that are typically mediated though photographic documentation. Considering work by artists such as Thomas Hirschhorn, Tania Bruguera, and Sophie Calle, this course covers topics such as relational aesthetics, site-specificity and pedagogical interventions into public space.', 'To complete this course students must enrol in ARTHIST 700 A and B, or ARTHIST 700', 'null', 'null');
