INSERT INTO `arts` VALUES ('Art History', 'ARTHIST 730B', 'Exploring Pacific Art', 'Focuses on a range of Mori and Pacific art forms. Themes dealt with include indigenous and migrant voices, memory and notions of belonging, popular culture and its interface with gallery practices, and stereotypes and representation. These topics will be discussed alongside relevant Mori and Pacific writers and theorists, including Ngahuia Te Awekotuku, Albert Wendt and Epeli Hau’ofa.', 'Restriction: ARTHIST 732, 736', 'null', 'null');
INSERT INTO `arts` VALUES ('Art History', 'ARTHIST 731', 'Sites of Resistance', 'Focuses on issues and implications of colonialism and its role in relation to the creation and expression of cultural identities. Classes revolve around close discussions of key readings and their implications in relation to contemporary art practice. There will be particular emphasis on the mediums of film, video, photography, multimedia and performance. Topics include border art, gender issues and counter-curating.', 'Restriction: ARTHIST 712', 'null', 'null');
INSERT INTO `arts` VALUES ('Art History', 'ARTHIST 732', 'Topics in Pacific Art and Visual Culture', 'Focuses on a range of Pacific art forms and aspects of visual culture. Topics include indigenous and migrant voices, memory and notions of belonging, popular culture and its interface with gallery practice and stereotypes and representation. A range of art works and issues are discussed alongside relevant Pacific writers and theorists, including Ngahuia Te Awekotuku, Albert Wendt and Epeli Hau\'ofa.', 'Restriction: ARTHIST 730', 'null', 'null');
