INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 100', 'Images of Asia', 'An interdisciplinary introduction to the histories and cultures of East Asian societies, exploring their development, their engagement with each other over time, and what makes them the societies that they are today.', 'Restriction: HISTORY 135', 'null', 'null');
