INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 140G', 'New Zealand and Asia', 'Explores Asia and its interrelationship with New Zealand, including Asia\'s growing presence in New Zealand in all its manifestations, and the evolving political, social, economic, cultural, and strategic relations between this country and Asia. Topics will include historical and contemporary ties with Asia, Asian migration, literature, media and films. The course will focus especially on South-East and East Asia.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 200', 'Asian Identities', 'Students explore the changing and contested nature of Asian identities through readings of seminal scholarly and theoretical texts on each theme, combined with analysis of the ways these themes are reflected in film, fiction and other popular cultural texts. The four themes (nationalism; gender; minorities; and indigenous rights) and a concentration on post-1945 East and South-East Asia provide the focus.', 'Prerequisite: ASIAN 100 or 30 points in Gender Studies or 45 points in BGlobalSt courses', 'null', 'null');
