INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 204', 'Asian Diasporas', 'Focuses on three major diasporic groups in Asia: Chinese, Korean and Japanese. Comparisons will be made among the three diasporic groups of overseas Koreans, Japanese and Chinese in their migration patterns, modes of adaptation, and transnational life styles.', 'Prerequisite: 45 points at Stage I in BA courses or 45 points in BGlobalSt courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 300', 'Special Study', 'Independent student research conducted under the supervision of one or more lecturers.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
