INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 708', 'Religion in Modern Japanese Society', 'The aim of this course is to understand the role of religious beliefs, practices, and institutions in modern Japanese society. Topics to be covered include the “invention” of State Shinto and its role in nation-building, the decline of established temple Buddhism, the emergence and impact of new religious movements, and social conflict related to religion-state issues in the postwar period.', 'Restriction: JAPANESE 308', 'null', 'null');
