INSERT INTO `arts` VALUES ('Asian Studies', 'ASIAN 753B', 'Special Topic: Transnational North-East Asia: Challenges and Possibilities', 'This course aims to critically engage the current debates surrounding the concept and movement of “transnational Asia” and the possibility of reconciliation between China, Japan and the two Koreas. It examines the historical, cultural, and ideological sources and recent development of this new form of regionalism, as well as such challenges as Chinese hegemony and competing nationalisms in the region.', 'To complete this course students must enrol in ASIAN 753 A and B, or ASIAN 753', 'null', 'null');
