INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 100G', 'Beginning Modern Chinese 1', 'Introduces students to modern Standard Chinese (Mandarin, <i>Putonghua</i> ) through exercises and activities to develop speaking, listening, reading and writing skills. Also introduces the social and cultural background of the language.', 'Putonghua', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 101', 'Beginning Modern Chinese 2', 'Continues to develop students’ Chinese proficiency in speaking, listening, reading, writing skills and cultural literacy.', 'Prerequisite: CHINESE 100', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 130', 'Rethinking China', 'An introduction to the artistic, literary, historical and philosophical heritage of China, allowing students to engage with stimulating texts from historical times to the modern period. Taught in English.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 178', 'Chinese Study Abroad 1', 'Formal language study in an approved overseas institution where instruction is in Mandarin Chinese. May not be taken if a more advanced language acquisition course in this subject has previously been passed.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 200', 'Intermediate Modern Chinese 1', 'Further develops students’ listening, speaking, reading and writing proficiency. Students who successfully complete the course will be familiar with most of the topics related to everyday life and can communicate in a limited range of contexts.', 'Prerequisite: CHINESE 101', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 201', 'Intermediate Modern Chinese 2', 'Further develops students’ listening, speaking, reading and writing skills and introduces the formal register of the language. By the end of the course students can handle daily situations with increasing accuracy.', 'Prerequisite: CHINESE 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 202', 'Chinese for Heritage Speakers', 'Designed for students who have lived in a Mandarin-speaking environment, but who have limited ability to read and write in Mandarin. This course covers the basic vocabulary and grammatical structures of Modern Standard Chinese with a focus on reading and writing skills.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 203', 'China on Screen', 'The transformation of China’s contemporary cultures and communities can be charted through film. This course uses films from the 1930s until this century to examine the development and contestation of the Chinese nation. Several films will be compared with their literary originals in translation.', 'Prerequisite: 30 points from ASIAN 100, COMMS 100, CHINESE 130, FTVMS 100, 101, JAPANESE 150, KOREAN 120, MEDIA 101 or 45 points at Stage I in BA courses', 'null', 'null');
