INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 278', 'Chinese Study Abroad 2B', 'Formal study in Chinese in an approved overseas university. Enrolment requires the approval of the Programme Coordinator.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 300', 'Advanced Modern Chinese 1', 'Further develops students’ listening, speaking, reading and writing skills through exploring Chinese culture and society. Builds on previous study of Chinese with an emphasis on developing independent skills to operate confidently in a Chinese-speaking environment.', 'Prerequisite: CHINESE 201', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 301', 'Advanced Modern Chinese 2', 'Builds on the knowledge gained from prior Chinese study and helps develop independent and confident skills as a proficient user of Chinese. Texts covering various aspects of Chinese society, culture and literature will be read to strengthen knowledge of Chinese grammar and vocabulary, develop skills in both written and spoken Chinese, and provide a deeper understanding of contemporary Chinese culture.', 'Prerequisite: CHINESE 300', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 302', 'Advanced Chinese Reading and Writing', 'Designed for students studying Chinese language at an advanced level, this course focuses on strengthening reading and writing skills.', 'Prerequisite: CHINESE 202 or 300 or approval by Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 303', 'China on Screen', 'The transformation of China’s contemporary cultures and communities can be charted through film. This course uses films from the 1930s until this century to examine the development and contestation of the Chinese nation. Several films will be compared with their literary originals in translation.', 'Prerequisite: 30 points at Stage II in Asian Studies or Media, Film and Television, or CHINESE 130 and 15 points at Stage II in Asian Studies', 'null', 'null');
