INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 724', 'Chinese Film and Popular Culture', 'Chinese feature films and other popular cultural phenomena (e.g., music, television, fashion, the internet) provide a medium for understanding a society undergoing rapid change. The emphasis is on contemporary developments, including youth cultures and Beijing from the 1960s to the present day. No knowledge of Chinese language required.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 727', 'Chinese New Zealanders', 'Examines both recent immigration trends and the historical development of the New Zealand Chinese and other Asian communities. Special attention will be paid to the impact on New Zealand\'s demographic profile, social and economic implications and race relations issues, and contemporary transnationalism in its historical context. Will also examine settlement and integration issues, and the tension between globalisation and New Zealand nationalism.\n</p>', 'null', 'null', 'null');
