INSERT INTO `arts` VALUES ('Chinese', 'CHINESE 735', 'Introduction to Chinese Linguistics', 'The phonology, written system, dialectology, semantics, morphology, syntax and rhetoric of Chinese; and an introduction to the terminology and methodology used in Chinese linguistics research. The focus will be on the development of students\' skills in critically appraising existing works and carrying out their own individual research projects.', 'Restriction: CHINESE 717', 'null', 'null');
