INSERT INTO `arts` VALUES ('Comparative Literature', 'COMPLIT 703B', 'Rethinking Literary Translation', 'Literary translation has come to be theorised as a dynamic and problematic process, central to comparative literature and shedding light on cross-cultural encounter, and colonisation and the post-colonial. Alongside such theoretical considerations students will undertake a practical translation project between languages in which they have expertise.', 'To complete this course students must enrol in COMPLIT 703 A and B, or COMPLIT 703', 'null', 'null');
