INSERT INTO `arts` VALUES ('Comparative Literature', 'COMPLIT 705', 'Reading Across Cultures', 'An advanced level review of approaches to the study of literature across cultures. Tests the potential and limits of theories of literature in the study of literary texts from many cultures and periods. Includes cross-cultural perspectives on authorship, intertextuality, reader-centred theories, literary translation, post-colonial literature, gender and sexuality, as well as reading across disciplines.', 'Restriction: COMPLIT 700, 709', 'null', 'null');
