INSERT INTO `arts` VALUES ('Criminology', 'CRIM 710', 'Cybercrime', 'Exploration of cybercrime and its economic and social impact. The course aims to encourage critical thinking, exploring a range of key theoretical perspectives in criminal justice and their application to cybercrime. It analyses how the Internet may promote criminal behaviour and contribute to the globalisation of crime. It also outlines the challenges of policing cybercrime, evaluating current approaches.\n</p>', 'null', 'null', 'null');
