INSERT INTO `arts` VALUES ('Development Studies', 'DEVELOP 701', 'Development Praxis', 'Focuses on the practice of development and its relation to theory. General topics include aid modalities, participatory development and social and environmental discourses and practices, and in those contexts the project cycle, programme management tools, monitoring and evaluation and impact assessment.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Development Studies', 'DEVELOP 702', 'Gender and Development', 'Considers a range of topics focusing on the centrality of gender to development and developing nations. Topics include: empowerment, reproduction, masculinities, health, fertility, gendered economies, micro-credit and familial resource allocation.\n</p>', 'null', 'null', 'null');
