INSERT INTO `arts` VALUES ('Development Studies', 'DEVELOP 705', 'Contemporary Issues in Gender and Development', 'Interrogates selected contemporary themes in gender and development. It builds on and complements DEVELOP 702 by focusing on the dynamics of topical issues in gender and development. In doing so it provides students with the critical tools of analysis necessary in research projects.', 'Prerequisite: DEVELOP 702', 'null', 'null');
