INSERT INTO `arts` VALUES ('Drama', 'DRAMA 100G', 'Presentation and Performance Skills: Taking the Stage', 'Focuses on enhancing oral communication and performance skills through interactive workshops with speakers and performers highlighting the transferable skills of acting in three main areas: public speaking, improvising and group-devised performance.\n</p>', 'null', 'null', 'null');
