INSERT INTO `arts` VALUES ('Drama', 'DRAMA 202B', 'History and Performance', 'Explores a range of major plays from ancient Greek tragedy to contemporary New Zealand drama. This course examines plays in their historical context and as texts for performance. In Semester Two, students learn skills in theatre production by staging a full-length play, directed by an outside professional director.', 'Prerequisite: 60 points passed and approval by the Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 203', 'New Zealand and Pacific Drama', 'An overview of the development of modern and contemporary drama, theatre and playwriting in New Zealand. Topics include: experimental theatre, feminist drama and Mori drama. Some plays by Pacific writers will be considered.', 'Prerequisite: 60 points passed', 'null', 'null');
