INSERT INTO `arts` VALUES ('Drama', 'DRAMA 709B', 'Studio - Level 9', 'A practical, explorative theatre or drama project, with written reflection, nominated by the student or a small group of students. Projects must be approved and supervised.', 'To complete this course students must enrol in DRAMA 709 A and B, or DRAMA 709', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 710', 'Semester One Production', 'Students participate in a full-scale, public production of a full-length play. As far as possible all roles from acting to lighting to design to stage-management to front of house etc will be taken by students enrolled in the course. Direction will be by teaching staff or guest director.', 'Restriction: DRAMA 703', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 711', 'Semester Two Production', 'Students participate in a full-scale public production of a full-length play. As far as possible all roles from acting to lighting to design to stage-management to front of house etc will be taken by students enrolled in the course. Direction will be by teaching staff or guest director.', 'Restriction: DRAMA 703', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 712', 'Directing Exercises', 'A number of established texts that present different challenges will be studied and students will carry out practical exercises on these texts in workshop style classes.', 'Restriction: DRAMA 704', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 713', 'Directing Project', 'Each student will carry out a project in directing either a short play or an excerpt from a play. No project will be longer than half an hour.', 'Restriction: DRAMA 704', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 716', 'Directed Study in Playwriting', 'A study of playwriting or workshopping or dramaturgy or a short writing project, either original or adaptation.\n</p>', 'null', 'null', 'null');
