INSERT INTO `arts` VALUES ('Drama', 'DRAMA 718', 'Playwriting', 'A series of exercises in the basic building blocks of dramatic writing, incorporating guest visits from practising and established dramatists.', 'Restriction: DRAMA 714', 'null', 'null');
