INSERT INTO `arts` VALUES ('Drama', 'DRAMA 727', 'Studies in Early Modern Theatre', 'An intensive study of practical and production aspects of the early modern English stage and staging, with particular attention to theories of drama and of acting and historical performance techniques.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Drama', 'DRAMA 728', 'Creative Research for Theatre', 'Introduces students to principles of postgraduate creative research for theatre. Combines critical reflection and practical exercises to explore best practice in contemporary performance research. Topics include developing a subject, refining a research question, using the creative process as research methodology, framing research findings. Issues of research, language, culture and gender are covered in terms of impact on creative research projects.\n</p>', 'null', 'null', 'null');
