INSERT INTO `arts` VALUES ('English', 'ENGLISH 782B', 'Interpreting Janet Frame', 'An exploration of the fiction of renowned writer Janet Frame. Using interpretative theory, the course addresses the challenge of developing enabling critical contexts for Frame’s novels. Conversely, Frame’s novels are used as a means of exploring the reading process and the dynamics involved in the act of interpretation.', 'Restriction: ENGLISH 710', 'null', 'null');
INSERT INTO `arts` VALUES ('English', 'ENGLISH 783', 'Studies in English Renaissance Drama', 'An advanced seminar on the intersection of literary and theatrical cultures in the English Renaissance period. Students will become acquainted with performance theories relating to the Renaissance stage, with particular attention paid to the relation between stage production and the production of meaning.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('English', 'ENGLISH 785', 'Directed Study', 'Supervised research on a topic or topics approved by the Academic Head or nominee.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('English', 'ENGLISH 787', 'Literature USA: from the American Renaissance to the Jazz Age', 'Examines a selection of classic texts and major issues in the literature of the United States from the American Renaissance of the 1840s and 1850s through to the Jazz Age of the 1920s and 1930s.\n</p>', 'null', 'null', 'null');
