INSERT INTO `arts` VALUES ('English Writing', 'ENGWRIT 101', 'English Writing for Academic Purposes', 'A skills-based analysis of texts written for academic purposes. Topics include: essays of comparison and contrast, argumentative essays, problem solution texts, literature reviews, critiques, and report writing.', 'Restriction: ENGWRIT 94F', 'null', 'null');
INSERT INTO `arts` VALUES ('English for Academic Purposes', 'ENGLACP 20P', 'English for Academic Purposes Level 1', 'Extensive reading of academic texts; writing different types of essays and reports, summary reviews; developing editing and referencing skills, understanding and avoiding plagiarism; oral presentations and group discussion skills and strategies, including pronunciation; developing and consolidating academic vocabulary and grammar of written discourse; monitoring and evaluating own learning; mastery of using electronic and print media learning and reference resources; understanding different English accents.', 'Restriction: ENGLACP 40P', 'null', 'null');
INSERT INTO `arts` VALUES ('English for Academic Purposes', 'ENGLACP 30P', 'English for Academic Purposes Level 2', 'Extensive and intensive reading of extended academic texts; developing skills of analysing, synthesising and critical commentary; writing longer essays and reports (1000 words); integrating and referencing source material; proof-reading and editing; avoiding plagiarism; oral seminar presentations; listening and note-taking from lectures in a subject of choice; orientation to student support and learning resources at the University of Auckland.', 'Prerequisite: ENGLACP 20P', 'null', 'null');
INSERT INTO `arts` VALUES ('English for Academic Purposes', 'ENGLACP 40P', 'English for Academic Purposes Level 3', 'Extensive and intensive reading of extended academic texts at postgraduate level; developing skills of analysing, synthesising and critical commentary; writing longer essays and reports integrating and referencing source material; proof-reading and editing; avoiding plagiarism; oral seminar presentations and discussions; listening and note-taking from lectures; orientation to student support and learning resources at the University of Auckland.', 'Prerequisite: ENGLACP 30P', 'null', 'null');
