INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 100G', 'Europe and the World', 'An introduction to the study of Europe, organised around a number of major themes, including linguistic and ethnic groupings, historical periods, literary and cultural movements, religious and philosophical traditions, and political and cultural figures. An ideal course for students wishing to explore European culture and civilisation.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 200', 'Screening Europe', 'Europe\'s rich and distinctive film tradition provides an opportunity to examine issues of contemporary Europe and its individual nations. This course examines the sources, complexities and resonances of a number of European films and the ways in which they refer, directly or obliquely, to historical, social and political issues around the concept of Europe. The films shown will all be subtitled.', 'Prerequisite: 30 points passed in BA or BGlobalSt courses', 'null', 'null');
INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 204', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 206', 'European Integration', 'This cross-disciplinary course examines political, economic, social and cultural integration and its effects in the fabric of contemporary Europe. Issues addressed include identity, immigration and citizenship in Europe, and matters pertaining to the European Union: its political form, enlargement, foreign and security policy, economic and monetary policy, and the European constitution.', 'Prerequisite: 30 points passed at Stage I', 'null', 'null');
INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 207', 'European Drama: Greatest Hits', 'Considers important themes and stylistic innovations in European drama through the study of a selection of great plays and playwrights from a number of European countries.', 'Prerequisite: 30 points passed in BA courses or Transnational Cultures and Creative Practice', 'null', 'null');
INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 208', 'Images of Men in Europe, 18th-21st Century', 'Focuses on the images of men in Europe, from the end of the eighteenth century to the present day, in the construction of European identity. The course examines changing representations of masculinity in European visual culture, particularly through sports and war, in relation to issues of consumption, medicine and sexuality.', 'Prerequisite: 30 points passed in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('European Studies', 'EUROPEAN 212', 'The History and Culture of War and Violence', 'Looks at the history and culture of war and violence through the ages with a particular focus on Europe. Themes may include: war and technology, war and society, war and ideology and the regulation of war and violence.', 'Prerequisite: 30 points at Stage I in BA or BGlobalSt courses', 'null', 'null');
