INSERT INTO `arts` VALUES ('French', 'FRENCH 304', 'Advanced French Language 1', 'Strengthens students\' command of reading, writing, speaking and listening in French. Organised thematically and uses both textual and audiovisual material to introduce students to a range of communicative registers. May not be taken if a more advanced language acquisition course in this subject has previously been passed.', 'Prerequisite: FRENCH 204', 'null', 'null');
INSERT INTO `arts` VALUES ('French', 'FRENCH 305', 'Advanced French Language 2', 'Further extends students\' French language skills through textual and audio-visual material, enabling them to attain a high level of oral and written proficiency.', 'Prerequisite: FRENCH 304', 'null', 'null');
INSERT INTO `arts` VALUES ('French', 'FRENCH 306', 'Medieval French Language and Culture: Love and Laughter in the Middle Ages', 'The main focus will be on language and literature, placing works in their historical and cultural contexts.', 'Prerequisite: FRENCH 304', 'null', 'null');
