INSERT INTO `arts` VALUES ('French', 'FRENCH 341', 'Panorama of the French Novel', 'A study of the changing forms of the novel from the representations of the seventeenth century court in Madame de Lafayette\'s <i>La Princesse de Clèves</i> to the twentieth century experiments of the French \'new novelists\'. Taught in French.', 'La Princesse de Clèves', 'null', 'null');
