INSERT INTO `arts` VALUES ('French', 'FRENCH 349', 'French Cinema Since the New Wave', 'An in-depth examination of major developments in French cinema since 1965, with a particular focus on the 1990s and beyond. This course presupposes a good working knowledge of film grammar, for example, shot analysis, mise en scène, editing techniques.', 'Prerequisite: FRENCH 304, or 30 points at Stage II in Media, Film and Television', 'null', 'null');
