INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 101', 'Gender: Global and Local', 'Develops an understanding of key concepts that underlie gender analysis, and how they are expressed in politics, culture and society. Examines the meaning of gender across a range of subjects and issues on the global stage and in our everyday lives.', 'Restriction: GENDER 100, WOMEN 100', 'null', 'null');
INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 202', 'Gender in the Pacific', 'Issues of gender politics, culture and migration in the contemporary Pacific. Considers the historical and contemporary development of gender identities and relations in and across Pacific cultures, as well as how various media such as film, music, photography, or other forms of cultural production influence gender in the region.', 'Prerequisite: 30 points passed', 'null', 'null');
