INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 208', 'Thinking Gender', 'Interdisciplinary examination of feminist theories of gender and sexuality. Focuses on contemporary issues, debates, and practices, while grounding them in foundational theories and investigating how these issues and debates play out across disciplines and practices. Examples include the perils of identity politics, tensions between feminist and queer theories, the politics of representation, and gender justice in a globalised world.', 'Prerequisite: 30 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 300', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 301', 'Gender, Sex and Commodification', 'Focuses on current and controversial issues at the intersections of sex and gender and their co-construction. Issues will be approached from contemporary feminist and queer theory perspectives. Various topics are critically examined in both theoretical and practical terms, such as co-constructions of gender and sexualities in pornography and advertising, technologies and reproduction, representations of transgender bodies/identities, and the selling of cybersex.', 'Prerequisite: 30 points at Stage II in Gender Studies or Sociology', 'null', 'null');
INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 306', 'Gender and Change: Making Waves', 'Explores the relationship between gender and other structures of inequality, like sexuality and ethno-race, and progressive social change. Develops and engages students’ theoretically informed critical skills in order to interrogate how gender inequality is re-produced, contested and/or transformed through all or some of the following: literary texts, visual representations, media texts, everyday practices and interactions, and policy.', 'Prerequisite: 30 points at Stage II in Gender Studies, or 15 points at Stage II in Gender Studies and 30 points passed at Stage II in BA courses', 'null', 'null');
