INSERT INTO `arts` VALUES ('Gender Studies', 'GENDER 700', 'Critical Theories and Methods in Gender Studies', 'Engages, using an interdisciplinary approach, critical theories and epistemological debates in gender studies; provides grounding in key gender studies methods and methodologies; traces the evolution in approaches to gender from early feminist scholarship to the present; requires independent research and application of theories and methods.\n</p>', 'null', 'null', 'null');
