INSERT INTO `arts` VALUES ('German', 'GERMAN 214', 'Teaching German as a foreign language', 'Provides an overview of teaching and learning German as a second or heritage language. Students gain a solid understanding of German teaching and learning, including concepts and theories behind second language learning, individual differences, learning needs, approaches to strengthen learner autonomy as well as methods and principles of teaching German.', 'Prerequisite: GERMAN 201 or equivalent', 'null', 'null');
INSERT INTO `arts` VALUES ('German', 'GERMAN 230', 'German Cinema from Murnau to Riefenstahl', 'A close analysis of film from the Weimar Republic and the Third Reich as seen against the political and social upheavals of the time with an emphasis on the role of gender and the portrayal of gender in film.', 'Prerequisite: Any 30 points passed in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('German', 'GERMAN 231', 'Modern Germany on Screen', 'Examines key issues and developments in German society through the medium of film. Surveying the years between 1945 and the present, films will focus on the reconstruction of Germany after WWII, the division and reunification of Germany as well as on current issues around immigration, identity and diversity in the German speaking world.', 'Prerequisite: 30 points passed in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('German', 'GERMAN 250', 'The Holocaust in Literature and Film', 'With a focus on German texts and films pertaining to World War II and the Holocaust, the course charts the development of the ideas and the language of genocide, and the representation of the Holocaust in literature and films.', 'Prerequisite: 30 points passed in BA courses', 'null', 'null');
