INSERT INTO `arts` VALUES ('German', 'GERMAN 301', 'German Language Advanced 1', 'Enables students to understand the main ideas of a wide range of complex texts on both concrete and abstract topics and to increase fluency both in oral and written communication. Brings students from proficiency level B1 to B2.1. The course is taught in German.', 'Prerequisite: GERMAN 201 or 203', 'null', 'null');
INSERT INTO `arts` VALUES ('German', 'GERMAN 302', 'German Language Advanced 2', 'German language acquisition at an advanced level. Advances students from B2.1 level to B2.2 level, i.e., completes the B2 proficiency stage in all four skills: reading, writing, speaking and listening. The course is taught in German.', 'Prerequisite: GERMAN 301', 'null', 'null');
INSERT INTO `arts` VALUES ('German', 'GERMAN 303', 'Contemporary German Literature', 'A study of post-1990 German literary texts as well as their social and political environment. Topics include: literary responses to a newly developing national identity, literary reconstructions of life in the GDR, literary representations of a united Germany by immigrants, Jewish identity in a united Germany. Authors considered include Jens Sparschuh, Monika Maron, Barbara Honigmann, Stefan Heym and others.', 'Prerequisite: GERMAN 201', 'null', 'null');
INSERT INTO `arts` VALUES ('German', 'GERMAN 305', 'Translation', 'The theory, practice and critical evaluation of translation, principally of texts translated from German into English. Not intended for students enrolled in the Postgraduate Diploma in Translation Studies.', 'Prerequisite: GERMAN 301', 'null', 'null');
