INSERT INTO `arts` VALUES ('History', 'HISTORY 260', 'The Mori 20th Century', 'Wide ranging study of Mori in the twentieth century exploring a variety of topics and themes including: studies and sources of Mori history; Mori and the state; war, work, church and leisure; resistance, protest and advocacy; rural and urban communities; organisations and leadership; mana wahine; and race relations in New Zealand.', 'Prerequisite: 15 points at Stage I in History and 30 points passed', 'null', 'null');
