INSERT INTO `arts` VALUES ('History', 'HISTORY 300', 'Thinking History: Approaches to the Past', 'Focuses on the study of history and how historians have understood and explained the past as well as the challenges facing the discipline today. Topics include post-structuralism and history, gender and history, the nature of historical memory and the impact of non-Western perspectives on the discipline.', 'Prerequisite: 15 points at Stage II in History and 60 points passed', 'null', 'null');
