INSERT INTO `arts` VALUES ('Humanities', 'HUMS 100G', 'Digital Humanities: From Text to txt', 'An interdisciplinary course designed to introduce students to the Humanities using digital tools and resources. Students will study the approaches, texts and digital technologies of disciplines in the Humanities such as Art History, English, History, Philosophy, and Theological and Religious Studies. Students will expand their knowledge of the Humanities, extend their digital literacy and build critical and creative thinking skills.', 'Restriction: ARTSGEN 100G', 'null', 'null');
INSERT INTO `arts` VALUES ('Humanities', 'HUMS 101', 'Europe: Medieval to Modern', 'A thematic and chronological survey of major developments in European history and visual culture since the early Middle Ages. The course is designed to provide a solid foundation for subsequent study in European history, art history and culture as well as an introduction to the nature of scholarly research and writing in the Humanities.', 'Restriction: ARTHIST 106, 107, HISTORY 106', 'null', 'null');
INSERT INTO `arts` VALUES ('Humanities', 'HUMS 300', 'Critiquing the Museum', 'An introduction to the history and theory of museums, and to institutional collecting and the interpretation of culture. Focuses on the role of museums in colonisation and nation building, involvement in globalising processes as well as the opportunities museums offer for social advocacy.', 'Prerequisite: 15 points in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Indigenous Studies', 'INDIGEN 700', 'Indigenous Theories', 'Topics include cultural autonomy, political inclusion, land claims, urbanisation and indigenous rights. Through a close reading of key texts and engaging in seminar discussions, students will deepen their insight into the knowledge systems that embody indigenous world views and be able to critically and analytically engage with historical and contemporary issues in Indigenous Studies.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Indigenous Studies', 'INDIGEN 701', 'Special Topic', '</p>', 'null', 'null', 'null');
