INSERT INTO `arts` VALUES ('Indigenous Studies', 'INDIGEN 710', 'Indigenous Methodologies', 'Research had been a powerful tool of colonisation. This course seeks to explore traditional and new methodologies that are robust, ethical and culturally informed to study Indigenous and Indigenous-related issues. In developing an understanding of relevant methodologies, ethics/tikanga and cultural understanding in Indigenous Studies students will gain knowledge of the links between Indigenous epistemology, research methodology and application.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Indigenous Studies', 'INDIGEN 711', 'Indigenous Environmental Politics', 'Examines contemporary issues related to Indigenous peoples and the environment. There will be a particular focus on the interface between Indigenous peoples, governments and corporate bodies. Topics may include Indigenous responses to environmental degradation; Indigenous peoples and extractive industries; sustainable development; Indigenous environmental protest movements; land and treaty rights; traditional knowledge and resource protection; and Indigenous peoples and climate change.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Indigenous Studies', 'INDIGEN 712', 'Indigenous Psychologies', 'Examines the historical and material circumstances Indigenous peoples face and the emergence and development of Indigenous psychologies to respond to a range of social and psychological challenges. Covers topics relevant to Indigenous and non-Indigenous survival and flourishing including cultural contributions to health and collective and individual wellbeing.\n</p>', 'null', 'null', 'null');
