INSERT INTO `arts` VALUES ('Indigenous Studies', 'INDIGEN 792B', 'Dissertation - Level 9', 'Examines key research issues for indigenous peoples. Students will develop a focused understanding of relevant methodologies, ethics and cultural understandings in Indigenous Studies. Students will develop a research project that identifies a particular indigenous issue and implement an appropriate methodology.', 'To complete this course students must enrol in INDIGEN 792 A and B, or INDIGEN 792', 'null', 'null');
