INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 100G', 'Introductory Italian Language', 'Learn basic Italian language structures and communication skills, including common words and basic phrases concerning everyday life. Acquire skills of interacting verbally by asking and answering straightforward questions on familiar topics. The course is delivered through a combination of class instruction and interactive online activities. For students with no previous knowledge of Italian.', 'Restriction: ITALIAN 106. May not be taken if an equivalent or more advanced language acquisition course in this subject has previously been passed.', 'null', 'null');
