INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 106G', 'Italian Language for Beginners 1', 'Students learn to speak, read and write Italian, studying aspects of contemporary Italian society and thought. This course does not count towards a major in Italian. For students with no previous knowledge of Italian.', 'Restriction: ITALIAN 100. May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 107', 'Italian Language for Beginners 2', 'Learners further develop lexical, grammatical, cultural and communicative competence, allowing them to prepare for intermediate level grammar and to be operative in social situations requiring a direct exchange of information in the target language environment and areas of immediate need. Meets the standard of Basic User level A2 as set out by the Council of Europe’s Language Policy Unit.', 'Prerequisite: ITALIAN 100 or 106', 'null', 'null');
INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 177', 'Study Abroad 1', 'Language course taken at an approved overseas institution where instruction is in Italian.', 'Prerequisite: ITALIAN 106', 'null', 'null');
INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 200', 'Intermediate Italian Language 1', 'Develops writing, reading, speaking and listening skills to an intermediate level, through practice on a wide range of written texts and current audio-visual material.', 'Prerequisite: ITALIAN 107', 'null', 'null');
INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 201', 'Intermediate Italian Language 2', 'Continues to develop language skills at an intermediate level.', 'Prerequisite: ITALIAN 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 202', 'Engendered Voices (Texts in Italian)', 'A critical study of the representation of women\'s experiences and of issues of gender and culture, through an examination of literary texts and films by Italian women writers and filmmakers. Students will study texts in Italian.', 'Prerequisite: ITALIAN 107', 'null', 'null');
INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 203', 'Engendered Voices (Texts in English)', 'A critical study of the representation of women\'s experiences and of issues of gender and culture, through an examination of literary texts and films by Italian women writers and filmmakers. Students will study texts in English. This course does not count towards a major or minor in Italian. Students taking an Italian major or minor should take ITALIAN 202 instead.', 'Prerequisite: 90 points passed', 'null', 'null');
