INSERT INTO `arts` VALUES ('Italian', 'ITALIAN 304', 'Foundations of European Literature (Texts in English)', 'Examines the founding texts of Italian literature and canonical books of Western culture: Dante’s <i>Divine Comedy,</i> a poetic summary of medieval learning, Boccaccio’s <i>Decameron,</i> a flawless human comedy and Petrarch’s <i>Canzoniere</i> (scattered rhymes), an intense examination of the self which became the model for love poetry in Western literature from Sidney and Shakespeare to the Romantic poets.', 'Divine Comedy,', 'null', 'null');
