INSERT INTO `arts` VALUES ('Japanese', 'JAPANESE 385', 'Topics in Japanese Culture and Society', 'Introduces several specific topics in modern Japanese society and culture. Topics may include: media, gender, ethnicity, colonialism, national identity, performing arts, and intellectual discourse. Readings are in Japanese and English.', 'Prerequisite: 45 points at Stage II in BA courses including JAPANESE 240, 241, 243 or 270', 'null', 'null');
INSERT INTO `arts` VALUES ('Japanese', 'JAPANESE 392', 'Special Topic', '', 'Prerequisite: JAPANESE 150 and 45 points at Stage II in Japanese', 'null', 'null');
INSERT INTO `arts` VALUES ('Japanese', 'JAPANESE 702', 'Topics in Japanese Culture and Society', 'In-depth study of selected topics on Japanese culture and society. Topics may include Japanese religion and society, popular culture, early modern history, Japan in East Asia and globalisation. This will be a team-taught course, and the content each year will be adapted according to student research interests. The course requires critical reading of core texts and regular student seminar presentations.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Japanese', 'JAPANESE 703', 'Topics in Japanese Language and Linguistics', 'In-depth study of selected topics on Japanese language and linguistics. Topics may include accentuation, morphophonology, transitivity, subjectivity, gender and language, politeness, and discourse strategies. The course focuses on student-led seminar presentations and discussions on assigned key works in the field.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Japanese', 'JAPANESE 706', 'Advanced Japanese Language Acquisition 1', 'Uses authentic materials on a variety of topics to enhance language skills in support of postgraduate studies.', 'Restriction: JAPANESE 707', 'null', 'null');
INSERT INTO `arts` VALUES ('Japanese', 'JAPANESE 707', 'Advanced Japanese Language Acquisition 2', 'Use materials on a variety of topics to enhance language skills in support of postgraduate studies. Readings relevant to the individual student’s research field will be assigned for critical analysis in Japanese.', 'Restriction: JAPANESE 706', 'null', 'null');
