INSERT INTO `arts` VALUES ('Korean', 'KOREAN 110G', 'Korean for Beginners 1', 'Basic written and spoken skills in modern Korean. Through the practice of listening to and reading basic Korean sentences, fundamental grammar and vocabulary are taught so that students will be able to carry out basic conversation and comprehend simple Korean texts.', 'Restriction: KOREAN 100, 250. May not be taken if a more advanced language acquisition course in this subject has previously been passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 111', 'Korean for Beginners 2', 'Further develops the basic proficiency in Korean necessary to communicate in limited situations. Uses a range of exercises and activities to develop speaking, listening, reading and writing skills. Introduces distinctive aspects of contemporary Korean culture related to language-use situations.', 'Prerequisite: KOREAN 110', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 120', 'Korean Society and Culture', 'An introduction to Korean society and culture, focusing on the development of the nation. The course covers the colonial legacy, national division, and cultural, social, economic and political changes in the two Koreas. No knowledge of Korean language required.\n</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 200', 'Intermediate Korean 1', 'Aims to expand students\' proficiency in Korean by introducing further points of grammar and their usage. This course serves as the base for oral and written language skills at an intermediate level.', 'Prerequisite: KOREAN 100 or 111', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 201', 'Intermediate Korean 2', 'A continuation of KOREAN 200 covering more advanced grammar points and their usage. Further develops communicative use of Korean in a wide range of everyday situations.', 'Prerequisite: KOREAN 200', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 203', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 205', 'Korea through TV Drama and Film', 'Examines some of the cultural, social and political issues of contemporary South Korea through a selection of popular TV dramas and films.', 'Prerequisite: 30 points at Stage I in Asian Studies, Chinese, Japanese or Korean, or 30 points from COMMS 100, FTVMS 100, 101, MEDIA 101 or 45 points at Stage I in BA courses', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 241', 'Modern Korea: Resilience, Innovation and Transformation', 'Aims to analyse historically the various ways in which the Koreans have understood, responded to and participated in the far-reaching changes which characterise Modern Korean history. The course is divided into three periods, the Late Choson (1800-1910), the Colonial Period (1905-1945), and the Era of Division (1945 to the present), and examines the chief internal and external forces that shaped the Korean nation up to the early 2000s.', 'Prerequisite: 15 points passed', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 250', 'Korean for Heritage Speakers', 'Aims to enhance written skills in modern Korean for students with native speaker background. Emphasis will be placed on the comprehension of a wide range of issues in Korean society.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 277', 'Korean Study Abroad 2A', 'Refer to the entry for Language Study Abroad.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 278', 'Korean Study Abroad 2B', 'Refer to the entry for Language Study Abroad.', 'Prerequisite: KOREAN 277 and approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 300', 'Advanced Korean 1', 'Korean grammar at an advanced level. A continuation of KOREAN 201.', 'Prerequisite: KOREAN 201', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 301', 'Advanced Korean 2', 'Designed to emphasise comprehension and composition of Korean texts.', 'Prerequisite: KOREAN 300', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 305', 'Korea through TV Drama and Film', 'Examines some of the cultural, social and political issues of contemporary South Korea through a selection of popular TV dramas and films.', 'Prerequisite: 30 points at Stage II in Asian Studies, Chinese, Japanese or Korean or 30 points at Stage II in Media, Film and Television', 'null', 'null');
INSERT INTO `arts` VALUES ('Korean', 'KOREAN 341', 'Modern Korea: Resilience, Innovation and Transformation', 'Aims to analyse historically the various ways in which the Koreans have understood, responded to and participated in the far-reaching changes which characterise Modern Korean history. The course is divided into three periods, the Late Choson (1800-1910), the Colonial Period (1905-1945), and the Era of Division (1945 to the present), and examines the chief internal and external forces that shaped the Korean nation up to the early 2000s.', 'Prerequisite: KOREAN 120 and 15 points at Stage II in Asian Studies or History', 'null', 'null');
